import { useState, useEffect } from 'react';
import MessageCard from '../MessageCard';
import { GET } from '../../utils/api';
import './index.css';

const MessageCardList = ({ isRenderedList, setRenderedList, filteredValue }) => {
  const [messageList, setMessageList] = useState([]);

  useEffect(() => {
      GET('messages').then((data) => setMessageList(data));
  }, [isRenderedList]);

  return (
    <div className="MessageCardList">
      {
        messageList.length
          ? messageList.filter((el)=>el.sender.toLowerCase().includes(filteredValue.toLowerCase())).map(message => <MessageCard isRenderedList={isRenderedList} onDeleteBtn={setRenderedList} textContent={ message } key={ message.id }/>)
          : <p>Loading...</p>
      }
    </div>
  )
}

export default MessageCardList;
